"use strict";

const React = require('react')
const PropTypes = require('prop-types')

class To extends React.Component {
  render() {
    return null;
  }

  stepTo() {
    const {
      history
    } = this.context.step;
    const {
      task
    } = this.props; // console.log(history, task);

    history.push(task);
  }

  componentDidMount() {
    // console.log('To mount')
    this.stepTo();
  }

  componentDidUpdate() {
    // console.log('To update')
    this.stepTo();
  }
}

To.contextTypes = {
  step: PropTypes.object
}


module.exports = To;